function PntH = getH_2_Reg( G_13Idx, Vertex_Crdnt, A, G_13rows, mu_r, x_max_vertex, y_max_vertex, z_max_vertex, CrossTex )
    
    PntH = zeros(8, 3);

    % Cpnts = PntsIdx(2, 5);
    switch CrossTex
        case 'XZ'
            PntH(1, :) = calH_2( G_13Idx(12), G_13Idx(3), G_13Idx(6), G_13Idx(7), ...
                                G_13rows(12, :), vertcat(G_13rows(3, :), G_13rows(6, :), G_13rows(7, :)), A, Vertex_Crdnt, mu_r, 1, x_max_vertex, y_max_vertex, z_max_vertex, 'Regular' );
            PntH(2, :) = calH_2( G_13Idx(11), G_13Idx(12), G_13Idx(10), G_13Idx(7), ...
                                G_13rows(11, :), vertcat(G_13rows(12, :), G_13rows(10, :), G_13rows(7, :)), A, Vertex_Crdnt, mu_r, 1, x_max_vertex, y_max_vertex, z_max_vertex, 'Regular' );
            PntH(3, :) = calH_2( G_13Idx(2), G_13Idx(11), G_13Idx(5), G_13Idx(7), ...
                                G_13rows(2, :), vertcat(G_13rows(11, :), G_13rows(5, :), G_13rows(7, :)), A, Vertex_Crdnt, mu_r, 1, x_max_vertex, y_max_vertex, z_max_vertex, 'Regular' );
            PntH(4, :) = calH_2( G_13Idx(3), G_13Idx(2), G_13Idx(1), G_13Idx(7), ...
                                G_13rows(3, :), vertcat(G_13rows(2, :), G_13rows(1, :), G_13rows(7, :)), A, Vertex_Crdnt, mu_r, 1, x_max_vertex, y_max_vertex, z_max_vertex, 'Regular' );
            PntH(5, :) = calH_2( G_13Idx(12), G_13Idx(3), G_13Idx(9), G_13Idx(7), ...
                                G_13rows(12, :), vertcat(G_13rows(3, :), G_13rows(9, :), G_13rows(7, :)), A, Vertex_Crdnt, mu_r, 1, x_max_vertex, y_max_vertex, z_max_vertex, 'Regular' );
            PntH(6, :) = calH_2( G_13Idx(11), G_13Idx(12), G_13Idx(13), G_13Idx(7), ...
                                G_13rows(11, :), vertcat(G_13rows(12, :), G_13rows(13, :), G_13rows(7, :)), A, Vertex_Crdnt, mu_r, 1, x_max_vertex, y_max_vertex, z_max_vertex, 'Regular' );
            PntH(7, :) = calH_2( G_13Idx(2), G_13Idx(11), G_13Idx(8), G_13Idx(7), ...
                                G_13rows(2, :), vertcat(G_13rows(11, :), G_13rows(8, :), G_13rows(7, :)), A, Vertex_Crdnt, mu_r, 1, x_max_vertex, y_max_vertex, z_max_vertex, 'Regular' );
            PntH(8, :) = calH_2( G_13Idx(3), G_13Idx(2), G_13Idx(4), G_13Idx(7), ...
                                G_13rows(3, :), vertcat(G_13rows(2, :), G_13rows(4, :), G_13rows(7, :)), A, Vertex_Crdnt, mu_r, 1, x_max_vertex, y_max_vertex, z_max_vertex, 'Regular' );
        case 'XY'
            PntH(1, :) = calH_2( G_13Idx(6), G_13Idx(9), G_13Idx(3), G_13Idx(7), ...
                                G_13rows(6, :), vertcat(G_13rows(9, :), G_13rows(3, :), G_13rows(7, :)), A, Vertex_Crdnt, mu_r, 1, x_max_vertex, y_max_vertex, z_max_vertex, 'Regular' );
            PntH(2, :) = calH_2( G_13Idx(9), G_13Idx(8), G_13Idx(4), G_13Idx(7), ...
                                G_13rows(9, :), vertcat(G_13rows(8, :), G_13rows(4, :), G_13rows(7, :)), A, Vertex_Crdnt, mu_r, 1, x_max_vertex, y_max_vertex, z_max_vertex, 'Regular' );
            PntH(3, :) = calH_2( G_13Idx(8), G_13Idx(5), G_13Idx(2), G_13Idx(7), ...
                                G_13rows(8, :), vertcat(G_13rows(5, :), G_13rows(2, :), G_13rows(7, :)), A, Vertex_Crdnt, mu_r, 1, x_max_vertex, y_max_vertex, z_max_vertex, 'Regular' );
            PntH(4, :) = calH_2( G_13Idx(5), G_13Idx(6), G_13Idx(1), G_13Idx(7), ...
                                G_13rows(5, :), vertcat(G_13rows(6, :), G_13rows(1, :), G_13rows(7, :)), A, Vertex_Crdnt, mu_r, 1, x_max_vertex, y_max_vertex, z_max_vertex, 'Regular' );
            PntH(5, :) = calH_2( G_13Idx(6), G_13Idx(9), G_13Idx(12), G_13Idx(7), ...
                                G_13rows(6, :), vertcat(G_13rows(9, :), G_13rows(12, :), G_13rows(7, :)), A, Vertex_Crdnt, mu_r, 1, x_max_vertex, y_max_vertex, z_max_vertex, 'Regular' );
            PntH(6, :) = calH_2( G_13Idx(9), G_13Idx(8), G_13Idx(13), G_13Idx(7), ...
                                G_13rows(9, :), vertcat(G_13rows(8, :), G_13rows(13, :), G_13rows(7, :)), A, Vertex_Crdnt, mu_r, 1, x_max_vertex, y_max_vertex, z_max_vertex, 'Regular' );
            PntH(7, :) = calH_2( G_13Idx(8), G_13Idx(5), G_13Idx(11), G_13Idx(7), ...
                                G_13rows(8, :), vertcat(G_13rows(5, :), G_13rows(11, :), G_13rows(7, :)), A, Vertex_Crdnt, mu_r, 1, x_max_vertex, y_max_vertex, z_max_vertex, 'Regular' );
            PntH(8, :) = calH_2( G_13Idx(5), G_13Idx(6), G_13Idx(10), G_13Idx(7), ...
                                G_13rows(5, :), vertcat(G_13rows(6, :), G_13rows(10, :), G_13rows(7, :)), A, Vertex_Crdnt, mu_r, 1, x_max_vertex, y_max_vertex, z_max_vertex, 'Regular' );
        case 'YZ'
            PntH(1, :) = calH_2( G_13Idx(4), G_13Idx(13), G_13Idx(8), G_13Idx(7), ...
                                G_13rows(4, :), vertcat(G_13rows(13, :), G_13rows(8, :), G_13rows(7, :)), A, Vertex_Crdnt, mu_r, 1, x_max_vertex, y_max_vertex, z_max_vertex, 'Regular' );
            PntH(2, :) = calH_2( G_13Idx(13), G_13Idx(10), G_13Idx(11), G_13Idx(7), ...
                                G_13rows(13, :), vertcat(G_13rows(10, :), G_13rows(11, :), G_13rows(7, :)), A, Vertex_Crdnt, mu_r, 1, x_max_vertex, y_max_vertex, z_max_vertex, 'Regular' );
            PntH(3, :) = calH_2( G_13Idx(10), G_13Idx(1), G_13Idx(5), G_13Idx(7), ...
                                G_13rows(10, :), vertcat(G_13rows(1, :), G_13rows(5, :), G_13rows(7, :)), A, Vertex_Crdnt, mu_r, 1, x_max_vertex, y_max_vertex, z_max_vertex, 'Regular' );
            PntH(4, :) = calH_2( G_13Idx(1), G_13Idx(4), G_13Idx(2), G_13Idx(7), ...
                                G_13rows(1, :), vertcat(G_13rows(4, :), G_13rows(2, :), G_13rows(7, :)), A, Vertex_Crdnt, mu_r, 1, x_max_vertex, y_max_vertex, z_max_vertex, 'Regular' );
            PntH(5, :) = calH_2( G_13Idx(4), G_13Idx(13), G_13Idx(9), G_13Idx(7), ...
                                G_13rows(4, :), vertcat(G_13rows(13, :), G_13rows(9, :), G_13rows(7, :)), A, Vertex_Crdnt, mu_r, 1, x_max_vertex, y_max_vertex, z_max_vertex, 'Regular' );
            PntH(6, :) = calH_2( G_13Idx(13), G_13Idx(10), G_13Idx(12), G_13Idx(7), ...
                                G_13rows(13, :), vertcat(G_13rows(10, :), G_13rows(12, :), G_13rows(7, :)), A, Vertex_Crdnt, mu_r, 1, x_max_vertex, y_max_vertex, z_max_vertex, 'Regular' );
            PntH(7, :) = calH_2( G_13Idx(10), G_13Idx(1), G_13Idx(6), G_13Idx(7), ...
                                G_13rows(10, :), vertcat(G_13rows(1, :), G_13rows(6, :), G_13rows(7, :)), A, Vertex_Crdnt, mu_r, 1, x_max_vertex, y_max_vertex, z_max_vertex, 'Regular' );
            PntH(8, :) = calH_2( G_13Idx(1), G_13Idx(4), G_13Idx(3), G_13Idx(7), ...
                                G_13rows(1, :), vertcat(G_13rows(4, :), G_13rows(3, :), G_13rows(7, :)), A, Vertex_Crdnt, mu_r, 1, x_max_vertex, y_max_vertex, z_max_vertex, 'Regular' );
    end

end